"use strict";$(function(){var j=$(":input");for(var e=0;e<j.length;e++){var k=j[e];if(k.hasAttribute("required")){var c=k.parentElement;var l=c;var b=$("label",l);if(b!==null||typeof(b)!=="undefined"){var d=b.html();if(exists(d)){if(d.indexOf("*")===-1){b.html(d+'<i style="color:red;margin-left:5px">*</i>')}}}}if(k.hasAttribute("type")){var g=k.getAttribute("type");if(exists(g)&&g==="number"){if(!k.hasAttribute("min")){var f=document.createAttribute("min");f.value=0;k.setAttributeNode(f);var h=$(k);var a=h.parent();if(!a.hasClass("input-group")){h.after('<span style="color:red;display:none">this value can not be negative,and has to be a number</span>')}else{a.parent().siblings("span").html("this value can not be negative,and has to be a number")}h.on("keyup",function(){var m=$(this);var i=m.val();if(exists(i)){if(i<0||!$.isNumeric(i)){m.css("border","2px solid red");var n=m.parent();if(!n.hasClass("input-group")){m.siblings("span").show()}else{n.siblings("span").show()}var o=setTimeout(function(){m.css("border","1px solid #ccc");if(!n.hasClass("input-group")){m.siblings("span").hide()}else{n.siblings("span").hide()}o=null},5000)}}})}}}}});function showValidationMessage(e,c){var a=e.siblings("label");var b=a.html();e.after('<p style="color:red;display:none">'+typeof(b)==="undefined"?"":b+"  "+c+"</p>");e.css("border","2px solid red");e.siblings("p").show();var d=setTimeout(function(){e.css("border","1px solid #ccc");e.siblings("p").hide;d=null},5000)};